CREATE TABLE "FINANCIAL"."STOCK_PRICES" (
  "TICKER" VARCHAR2(10 CHAR) PRIMARY KEY,
  "COMPANY_NAME" VARCHAR2(255 CHAR),
  "CURRENT_PRICE" NUMBER(10, 2),
  "LAST_UPDATED" TIMESTAMP DEFAULT SYSTIMESTAMP
);


CREATE TABLE "FINANCIAL"."STOCK_PURCHASES" (
  "PURCHASE_ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY PRIMARY KEY,
  "CUSTOMER_ID" VARCHAR2(255 CHAR),
  "TICKER" VARCHAR2(10 CHAR),
  "QUANTITY" NUMBER(10, 0),
  "PURCHASE_PRICE" NUMBER(10, 2),
  "PURCHASE_DATE" TIMESTAMP DEFAULT SYSTIMESTAMP,
--  CONSTRAINT "FK_PURCHASE_CUSTOMER"
--    FOREIGN KEY ("CUSTOMER_ID")
--    REFERENCES "FINANCIAL"."ACCOUNTS" ("CUSTOMER_ID"),
  CONSTRAINT "FK_PURCHASE_TICKER"
    FOREIGN KEY ("TICKER")
    REFERENCES "FINANCIAL"."STOCK_PRICES" ("TICKER")
);

